frappe.ui.form.on('ToDo', {
	refresh(frm) {
		// your code here
	},
	date(frm){
	    if(frm.doc.custom_start_date && frm.doc.date){
	        let custom_start_date = new Date(frm.doc.custom_start_date);
	        let date = new Date(frm.doc.date)
	        if (custom_start_date > date){
	            frappe.throw("Due Date should be always more than Start Date")
	            frappe.validated = false;
	        }
	    }
	},
	custom_start_date(frm){
        if(frm.doc.custom_start_date && frm.doc.date){
            let custom_start_date = new Date(frm.doc.custom_start_date);
            let date = new Date(frm.doc.date)
            if (custom_start_date > date){
                frappe.throw("Extended Date should be always more than Start Date")
                frappe.validated = false;
            }
        }
	},
	validate(frm){
	    if(frm.doc.custom_start_date && frm.doc.date){
	        let custom_start_date = new Date(frm.doc.custom_start_date);
	        let date = new Date(frm.doc.date)
	        if (custom_start_date > date){
	            frappe.throw("Extended Date should be always more than MoU End Date")
	            frappe.validated = false;
	        }
	    }
	}
})

