script:
frappe.ui.form.on('ToDo', {
	refresh(frm) {
		// your code here
	},
	date(frm){
	    if(frm.doc.custom_start_date && frm.doc.date){
	        let custom_start_date = new Date(frm.doc.custom_start_date);
	        let date = new Date(frm.doc.date)
	        if (custom_start_date > date){
	            frappe.throw({message:"Due Date should be always more than Start Date"})
	            frappe.validated = false;
	        }
	    }
	},
	custom_start_date(frm){
        if(frm.doc.custom_start_date && frm.doc.date){
            let custom_start_date = new Date(frm.doc.custom_start_date);
            let date = new Date(frm.doc.date)
            if (custom_start_date > date){
                frappe.throw({message:"Due Date should be always more than Start Date"})
                frappe.validated = false;
            }
        }
	},
	validate(frm){
	    if(frm.doc.custom_start_date && frm.doc.date){
	        let custom_start_date = new Date(frm.doc.custom_start_date);
	        let date = new Date(frm.doc.date)
	        if (custom_start_date > date){
	            frappe.throw({message:"Due Date should be always more than Start Date"})
	            frappe.validated = false;
	        }
	    }
	}
})

frappe.ui.form.on('Contact', {
	refresh(frm) {

	},
	validate(frm){
	    if(frm.doc.custom_whatsapp_number.length == 10){
	        validate_indian_phone_number_regex(frm.doc.custom_whatsapp_number)
	    }
	},
	custom_whatsapp_number(frm){
	    if(frm.doc.custom_whatsapp_number.length == 10){
	        validate_indian_phone_number_regex(frm.doc.custom_whatsapp_number)
	    }
	}
})

frappe.listview_settings['ToDo'] = {
    formatters: {
        date: function(value, data, row) {
            let due_date = new Date(value);
            let today = new Date();

            due_date.setHours(0, 0, 0, 0);
            today.setHours(0, 0, 0, 0);

            console.log(row,'data : value');

            if (due_date < today && !['Cancelled','Done'].includes(row.custom_task_status)) {
                return `<span class="text-danger">${value}</span>`;
            }
            return value;
        }
    }
};
