script:
async function fetchData() {
    const data = await new Promise((resolve, reject) => {
        frappe.call({
            method: "frappe.client.get_list",
            args: {
                doctype: "mGrant Task",
                fields: ["*"],
                limit_page_length: 20
            },
            callback: function(response) {
                if (response.message) {
                    resolve(response.message);
                } else {
                    reject("No data found");
                }
            }
        });
    });

    return data;
}

function renderCards(data) {
    const cardsContainer = root_element.querySelector("#cards");
    cardsContainer.innerHTML = ""; // Clear existing content

    data.forEach(task => {
        const card = `
                  <div class="col-lg-4 col-md-6 col-sm-12 mb-2">
                        <div class="card border-light shadow-sm" style="padding: 16px;">
                            <!-- Task Header -->
                            <div class="d-flex justify-content-between align-items-center">
                                <span title="task title" class="text-dark me-2" style="font-size: 16px; font-weight: 400; line-height: 17.6px; letter-spacing: 0.5%; color: #000;">
                                    ${task.title}
                                </span>
                                <div class="d-flex align-items-center" style="gap: 24px;">
                                    <div class="dropdown">
                                        <p title="action" class="pointer pt-3" id="dropdownMenuButton-1" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                            <i class="fa fa-ellipsis-h mt-2" style="transform: rotate(90deg); font-size: 20px; width: 20px; height: 20px;"></i>
                                        </p>
                                        <div class="dropdown-menu" aria-labelledby="dropdownMenuButton-1">
                                            <a class="dropdown-item edit-btn" data-task="1">Edit</a>
                                            <a class="dropdown-item delete-btn" data-task="1">Delete</a>
                                        </div>
                                    </div>
                                    <input type="checkbox" class="toggleCheckbox form-check-input" data-id="1" style="width: 20px !important; height: 20px !important;">
                                </div>
                            </div>

                            <!-- User Info -->
                            <div class="d-flex align-items-center text-muted small" style="gap: 6px;">
                                <div class="avatar bg-primary text-white rounded-circle d-flex justify-content-center align-items-center me-2" style="width: 24px; height: 24px;">
                                    ${task.assigned_to ? task.assigned_to[0].toUpperCase() : '-'}
                                </div>
                                <span style="color: #6E7073; font-size: 12px; font-weight: 400; line-height: 13.2px;">
                                    ${ task.assigned_to ?? 'No Assignee'}
                                </span>
                            </div>
                            <p style="margin-top: 8px;max-height:40px;min-height:40px;overflow:hidden;" data-toggle="tooltip" data-placement="bottom" title="Task description" data-html="true">
                                ${task.description}
                            </p>

                            <!-- Task Priority and Status -->
                            <div class="d-flex align-items-center justify-content-between" style="gap: 12px;">
                                <div class="d-flex" style="gap: 10px;">
                                    <div class="dropdown" style="width: 100px; height: 26px; border-radius: 4px; background-color: #F1F1F1; color: #0E1116; font-weight: 400; font-size: 14px; line-height: 15.4px; letter-spacing: 0.25%; display: flex; align-items: center; justify-content: center; gap: 4px;">
                                        <span title="Priority" id="dropPriority-1" class="small dropdown-toggle badge bg-light pointer text-muted" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                            Low
                                        </span>
                                        <div class="dropdown-menu" aria-labelledby="dropPriority-1">
                                            <a class="dropdown-item task-priority" data-task="1" data-priority="Low">Low</a>
                                            <a class="dropdown-item task-priority" data-task="1" data-priority="Medium">Medium</a>
                                            <a class="dropdown-item task-priority" data-task="1" data-priority="High">High</a>
                                        </div>
                                    </div>

                                    <div class="dropdown" style="width: 100px; height: 26px; border-radius: 4px; background-color: #F1F1F1; color: #0E1116; font-weight: 400; font-size: 14px; line-height: 15.4px; letter-spacing: 0.25%; display: flex; align-items: center; justify-content: center; gap: 4px;">
                                        <span title="status" id="dropStatus-1" class="small dropdown-toggle bg-light pointer badge text-muted" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                            Status
                                        </span>
                                        <div class="dropdown-menu" aria-labelledby="dropStatus-1">
                                            <a class="dropdown-item task-status" data-task="1" data-status="Backlog">Backlog</a>
                                            <a class="dropdown-item task-status" data-task="1" data-status="Todo">Todo</a>
                                            <a class="dropdown-item task-status" data-task="1" data-status="In Progress">In Progress</a>
                                            <a class="dropdown-item task-status" data-task="1" data-status="Done">Done</a>
                                            <a class="dropdown-item task-status" data-task="1" data-status="Canceled">Canceled</a>
                                        </div>
                                    </div>
                                </div>

                                <span class="ms-auto small" style="color: #6E7073; font-size: 12px;">
                                    Grant: <span style="color: #0E1116;">${task.name}</span>
                                </span>
                            </div>

                            <!-- Due Date -->
                            <p class="small" style="color: #6E7073; padding-top: 16px;margin: 0;">
                                Due Date: <span class="text-danger"> ${task.due_date ??  '--:--'}</span>
                            </p>
                        </div>
                    </div>`;
        cardsContainer.insertAdjacentHTML("beforeend", card);
    });
}

// Fetch and render data
fetchData()
    .then(data => renderCards(data))
    .catch(error => console.error(error));

