script:
frappe.call({
    method: "myojana.sva_report.controllers.get_report_data.execute",
    args: { doc: "Beneficiary Profiling"},
    callback: function(response) {
        if (response.message && response.message.data && response.message.columns) {
            var allData = response.message.data; // All data from the API
            var columns = response.message.columns;

            // Pagination settings
            var pageSize = 50; // Number of items per page
            var currentPage = 1; // Current page number
            var totalItems = allData.length; // Total number of items
            var totalPages = Math.ceil(totalItems / pageSize); // Total number of pages

            // Function to render table rows for the current page
          function renderTableRows() {
            var startIndex = (currentPage - 1) * pageSize;
            var endIndex = Math.min(startIndex + pageSize, totalItems);
            var tableRows = '';

            // Fetch data for the current page
            var currentPageData = allData.slice(startIndex, endIndex);

            for (var i = 0; i < currentPageData.length; i++) {
                tableRows += "<tr>" + columns.map(function(column) {
                    return "<td>" + currentPageData[i][column.fieldname] + "</td>";
                }).join('') + "</tr>";
            }

            return tableRows;
        }
            // Function to render pagination controls
            function renderPagination() {
                var pagination = '<ul class="pagination">';

                // Previous button
                pagination += `<li class="page-item ${currentPage === 1 ? 'disabled' : ''}">
                                <a class="page-link" onclick="prevPage()">Previous</a>
                              </li>`;

                // Page numbers
                for (var i = 1; i <= totalPages; i++) {
                    pagination += `<li class="page-item ${currentPage === i ? 'active' : ''}">
                                    <a class="page-link" onclick="goToPage(${i})">${i}</a>
                                  </li>`;
                }

                // Next button
                pagination += `<li class="page-item ${currentPage === totalPages ? 'disabled' : ''}">
                                <a class="page-link" onclick="nextPage()">Next</a>
                              </li>`;

                pagination += '</ul>';

                return pagination;
            }

          // Define functions in the global scope or attach them to an object
                window.goToPage = function(page) {
                    currentPage = page;
                    render();
                };

                window.nextPage = function() {
                    if (currentPage < totalPages) {
                        currentPage++;
                        render();
                    }
                };

                window.prevPage = function() {
                    if (currentPage > 1) {
                        currentPage--;
                        render();
                    }
                };

            // Function to render the table and pagination
            function render() {
                var table = `<table class="table"><thead><tr>${columns.map(column => `<th>${column.label}</th>`).join('')}</tr></thead><tbody>${renderTableRows()}</tbody></table>`;

                var pagination = renderPagination();

                // Append the generated table and pagination to the table element
                var tableElement = root_element.getElementById('table');
                var paginationElement = root_element.getElementById('pagination');
                tableElement.innerHTML = table;
                pageSize < 51 ? "" : paginationElement.innerHTML = pagination;
            }

            // Initial render
            render();

        } else {
            console.error("Invalid data returned from the API");
        }
    }
});


root_element.getElementById("export-button").addEventListener("click", function() {
    // Make API call to export data
    frappe.call({
        method: "myojana.sva_report.controllers.get_report_data.execute",
        args: {doc: "Beneficiary Profiling", "csv_exporz": 1},    
        callback: function(response) {
            // Handle response
            if (response.message) {
                // Data exported successfully, handle download
                downloadTableAsCSV(response.message.data, "exported_data.csv");
            } else {
                // Error occurred, handle it here
                alert("Error exporting data: " + response.exc);
            }
        }
    });
});

function downloadTableAsCSV(data, filename) {
    // Convert data to CSV format
    var csv = convertToCSV(data);

    // Create a Blob
    var blob = new Blob([csv], { type: 'text/csv' });

    // Create a temporary link
    var link = document.createElement("a");
    link.href = window.URL.createObjectURL(blob);
    link.download = filename;

    // Trigger download
    document.body.appendChild(link);
    link.click();
}

function convertToCSV(data) {
    var csv = '';
    // Extract column headers
    var headers = Object.keys(data[0]);
    csv += headers.join(',') + '\n';

    // Extract rows
    data.forEach(function(row) {
        var values = headers.map(function(header) {
            return row[header];
        });
        csv += values.join(',') + '\n';
    });

    return csv;
}

