response = frappe.get_all(
    'DocField',
    filters={'options': 'Proposal'},
    fields=['parent', 'fieldname']
)
dts = [res['parent'] for res in response]

if doc.ref_doctype in dts:
    for el in response:
        if el['parent'] == doc.ref_doctype:
            curr_doc = frappe.get_doc(doc.ref_doctype, doc.docname)
            if curr_doc.get(el['fieldname']):
                doc.custom_actual_doctype = doc.ref_doctype
                doc.custom_actual_document_name = doc.docname
                doc.ref_doctype = 'Proposal'
                doc.docname = curr_doc.get(el['fieldname'])

response = frappe.get_all(
    'DocField',
    filters={'options': 'Grant'},
    fields=['parent', 'fieldname']
)

dts = [res['parent'] for res in response]

if doc.ref_doctype in dts:
    for el in response:
        if el['parent'] == doc.ref_doctype:
            curr_doc = frappe.get_doc(doc.ref_doctype, doc.docname)
            if curr_doc.get(el['fieldname']):
                doc.custom_actual_doctype = doc.ref_doctype
                doc.custom_actual_document_name = doc.docname
                doc.ref_doctype = 'Grant'
                doc.docname = curr_doc.get(el['fieldname'])

if doc.custom_start_date and doc.date:
    if frappe.utils.getdate(doc.custom_start_date) > frappe.utils.getdate(doc.date):
        frappe.throw("Due Date should always be greater than Start Date")


# Compare with the current date
if frappe.utils.getdate(doc.date) < frappe.utils.getdate(frappe.utils.today()):
    doc.custom_task_status = 'Delayed'
    frappe.msgprint('Task status has been changed to Delayed as the due date has passed.');
